import React, { Component } from "react";
import firebase from './firebase'

const dbRef = firebase.database().ref();

class ShowColumn extends Component {
  handleDelete = (event) => {
    dbRef.child(event.target.id).remove();
  }

  render() {
    const finalIncomeTotal = (this.props.incomeArray.reduce((a, b) => parseFloat(a) + parseFloat(b), 0)).toFixed(2)
    const finalExpenseTotal = (this.props.expenseArray.reduce((a, b) => parseFloat(a) + parseFloat(b), 0)).toFixed(2)
    const finalTotal = (finalIncomeTotal - finalExpenseTotal).toFixed(2);

    return (
      <div>
        <div className="flexParent">
          <div>
            <h2> Income </h2>
            <ul>
              {this.props.listTransaction.map((transaction, i) => {
                const isType = transaction.transactionObject.type === "income"
                return (
                  (isType) ?

                    <li key={i}> 
                    <i className="fa fa-minus-circle" aria-hidden="true" 
                    id={transaction.transactionKey} onClick={this.handleDelete}> </i>  {transaction.transactionObject.description
                    } : {transaction.transactionObject.amount} </li>

                    : null
                )
              })}
            </ul>
            <h4 className="colorPop"> Total : $ {finalIncomeTotal} </h4>
          </div>
          <div>
            <h2> Expenses </h2>
            <ul>
              {this.props.listTransaction.map((transaction, i) => {
                const isType = transaction.transactionObject.type === "expense"
                return (
                  (isType) ?

                    <li key={i}> 
                    <i className="fa fa-minus-circle" aria-hidden="true" id={transaction.transactionKey} onClick={this.handleDelete}></i>  {transaction.transactionObject.description
                    } : {transaction.transactionObject.amount} </li>

                    : null
                )
              })}
            </ul>
            <h4 className="colorPop"> Total: $ {finalExpenseTotal} </h4>
          </div>
        </div>
        <div>
          {finalTotal >= 0 ?

            <h3 className="colorPositive"> Balance: $ {finalTotal} </h3>

            :

            <h3 className="colorNegative"> Balance: $ {finalTotal} </h3>}
        </div>
      </div>
    )
  }
}

export default ShowColumn;
