import React, { Component } from "react";
import Chart from "chart.js";

class Result extends Component {
  chartRef = React.createRef();
  state = {
    labels: []
  }


  handleSubtotal = (array) => {
    return array.reduce((a, b) =>
      parseFloat(a) + parseFloat(b), 0
    ).toFixed(2)
  }
  componentDidUpdate() {
    const myChartRef = this.chartRef.current.getContext("2d");
    const {expenseArray, expenseAmountArray, expenseCat} = this.props
    new Chart(myChartRef, {
      type: "horizontalBar",
      data: {
        //Bring in data
        labels: expenseArray.map((data) => data.categories),
        datasets: [
          {
            label: "Expense Result",
            data: expenseArray.map((data) => data.amount),
            backgroundColor: [
              "rgba(255, 99, 132, 0.2)",
              "rgba(54, 162, 235, 0.2)",
              "rgba(255, 206, 86, 0.2)",
              "rgba(75, 192, 192, 0.2)",
              "rgba(153, 102, 255, 0.2)",
              "rgba(255, 159, 64, 0.2)"
            ],
            borderColor: [
              "rgba(255, 99, 132, 1)",
              "rgba(54, 162, 235, 1)",
              "rgba(255, 206, 86, 1)",
              "rgba(75, 192, 192, 1)",
              "rgba(153, 102, 255, 1)",
              "rgba(255, 159, 64, 1)"
            ],
            borderWidth: 1
          }
        ],
      },
      options: {
        //Customize chart options
        legend: {
          labels: {
            fontSize: 22
          }
        },
        scales: {
          xAxes: [
            {
              ticks: {
                beginAtZero: true,
                fontSize: 22
              }
            }
          ],
          yAxes: [
            {
              ticks: {
                fontSize: 22
              }
            }
          ]
        }
      }
    });
  }

  handleSubtotal = (array) => {
    return array.reduce((a, b) =>
      parseFloat(a) + parseFloat(b), 0
    ).toFixed(2)
  }

  handleTotal = () => {
    const arr1 = this.handleSubtotal(this.props.incomeAmountArray);
    const arr2 = this.handleSubtotal(this.props.expenseAmountArray);
    return (arr1 - arr2).toFixed(2)
  }

  a = () => {
    const group =
    this.props.expenseArray.reduce((r, a) => {
      // console.log("a", a);
      // console.log('r', r);
      r[a.categories] = [...r[a.categories] || [], a];
      return r;
      }, {});
      console.log("group", group);

      // const a = group.food.map((b) => {
      //   console.log(b);
      //   return b
      // })

      // console.log(a);
  }
  render() {
    this.a()
    // let group = this.props.expenseArray.reduce((r, a) => {
    //   // console.log("a", a);
    //   // console.log('r', r);
    //   r[a.categories] = [...r[a.categories] || [], a];
    //   return r;
    //   }, {});
    //   console.log("group", group);

    //   let a = group.food.map((b) => {
    //     console.log(b);
    //     return b
    //   })

    //   console.log(a);


    // console.log(this.props.expenseArray);

    // const array1 = this.reduceArray(this.props.expenseArray)
    // console.log(array1);

    return (
      <div>
        {this.handleTotal() >= 0 ?

          <h3 className="colorPositive"> Balance: $ {this.handleTotal()} </h3>

          :

        <h3 className="colorNegative"> Balance: $ {this.handleTotal()} </h3>}
        <canvas
          id="myChart"
          ref={this.chartRef}
      />
      </div>
    )
  }
}

export default Result
