import React, { Component } from "react";
import Chart from "chart.js";
// import classes from "./LineGraph.module.css";

class Result extends Component {
  handleSubtotal = (array) => {
    return array.reduce((a, b) =>
      parseFloat(a) + parseFloat(b), 0
    ).toFixed(2)
  }

  handleTotal = () => {
    const arr1 = this.handleSubtotal(this.props.incomeAmountArray);
    const arr2 = this.handleSubtotal(this.props.expenseAmountArray);
    return (arr1 - arr2).toFixed(2)
  }

  render() {
    return (
        <div>
          {this.handleTotal() >= 0 ?

            <h3 className="colorPositive"> Balance: $ {this.handleTotal()} </h3>

            :

            <h3 className="colorNegative"> Balance: $ {this.handleTotal()} </h3>}

        </div>
    )
  }
}

export default Result;

class LineGraph extends Component {
  chartRef = React.createRef();

  componentDidMount() {
    const myChartRef = this.chartRef.current.getContext("2d");

    new Chart(myChartRef, {
      type: "horizontalBar",
      data: {
        //Bring in data
        // labels: ["Jan", "Feb", "March"],

        labels: this.props.incomeArray.forEach((data)=> {
          return data
        }),
        datasets: [
          {
            label: "Sales",
            data: [86, 67, 91],
            backgroundColor: [
              "rgba(255, 99, 132, 0.2)",
              "rgba(54, 162, 235, 0.2)",
              "rgba(255, 206, 86, 0.2)",
              "rgba(75, 192, 192, 0.2)",
              "rgba(153, 102, 255, 0.2)",
              "rgba(255, 159, 64, 0.2)"
            ],
            borderColor: [
              "rgba(255, 99, 132, 1)",
              "rgba(54, 162, 235, 1)",
              "rgba(255, 206, 86, 1)",
              "rgba(75, 192, 192, 1)",
              "rgba(153, 102, 255, 1)",
              "rgba(255, 159, 64, 1)"
            ],
            borderWidth: 1
          }
        ],
      },
      options: {
        //Customize chart options
      }
    });
  }
  render() {
    return (
      <div>
        <canvas
          id="myChart"
          ref={this.chartRef}
        />
      </div>
    )
  }
}
export {LineGraph}
