import React, { Component } from "react";
import dbRef from './firebase'

class ShowColumn extends Component {
  handleDelete = (event) => {
    dbRef.child(event.target.id).remove();
  }

  handleSubtotal = (array) => {
    return array.reduce((a,b) => 
      parseFloat(a) + parseFloat(b), 0
    ).toFixed(2)
  }

  handleTotal = () => {
    const arr1 = this.handleSubtotal(this.props.incomeArray);
    const arr2 = this.handleSubtotal(this.props.expenseArray);
    return (arr1 - arr2).toFixed(2)
  }

  render() {
    const {transKey} = this.props.transList
    return (
      <div>
        <div className="flexParent">
          <div>
            <h2> Income </h2>
            <ul>
              {this.props.transList.map((trans, i) => {
                const {type, description, amount} = trans.transObject;               
                const isType = type === "income"
                return (
                  (isType) ?

                    <li key={i}> 
                    <i className="fa fa-minus-circle" aria-hidden="true" 
                    id={transKey} onClick={this.handleDelete}> </i>  {description
                    } : {amount} </li>

                    : null
                )
              })}
            </ul>
            <h4 className="colorPop"> Total : $ {this.handleSubtotal(this.props.incomeArray)} </h4>
          </div>
          <div>
            <h2> Expenses </h2>
            <ul>
              {this.props.transList.map((trans, i) => {
                const { type, description, amount } = trans.transObject; 
                const isType = type === "expense"
                return (
                  (isType) ?

                    <li key={i}> 
                    <i className="fa fa-minus-circle" aria-hidden="true" id={transKey} onClick={this.handleDelete}></i>  {description
                    } : {amount} </li>

                    : null
                )
              })}
            </ul>
            <h4 className="colorPop"> Total: $ {this.handleSubtotal(this.props.expenseArray)} </h4>
          </div>
        </div>
        <div>
          {this.handleTotal() >= 0 ?

            <h3 className="colorPositive"> Balance: $ {this.handleTotal()} </h3>

            :

            <h3 className="colorNegative"> Balance: $ {this.handleTotal()} </h3>}
        </div>
      </div>
    )
  }
}

export default ShowColumn;
